CREATE DEFINER=`devwork`@`%` PROCEDURE `SearchPosts`(IN `$text` TEXT, IN `$lang` VARCHAR(10))
BEGIN
	SELECT po.id as 'postId',
    po.postSlug, po.postThumb, po.createdAt,
    pd.postTitle, pd.postDesc
    FROM hcn_post po JOIN hcn_post_details pd
    ON po.id = pd.postId
    WHERE pd.postLang = $lang
    AND po.postStatus = 1
    AND pd.postContent IS NOT NULL
    AND (pd.postTitle LIKE CONCAT('%', $text , '%') OR pd.postDesc LIKE CONCAT('%', $text , '%')
         OR pd.postContent LIKE CONCAT('%', $text , '%'))
    ORDER BY po.createdAt DESC;
END$$

