INSERT INTO `hcn_post_details` (`id`, `postId`, `postTitle`, `postContent`, `postDesc`, `postLang`, `postTags`, `postKeywords`) VALUES
(148, '20200323094546', 'Hachinet社員の最初の4名がBlockchainのトレーニング修了証書を取得', , '第四次産業革命インダストリー4.0において、ブロックチェーンはデジタルテクノロジーの“キー“と言われ、未来のテクノロジーベースの構築に貢献するものです。そこで、２０１８年10月03日付き、Hachinet社は、Sophiaソフィア先端技術学院により開催されたBlockchain のトレーニングコースに、エンジニアの最初の３名を参加させました。本コースは1ヶ月半に行われ、講義が12回しかありません。自学習時間は主になります。', 'jp', NULL, NULL),
(149, '20200323094546', 'The first 4 Hachinet programmers receive prestigious Blockchain training certificates.', , 'In October, Hachinet company boldly brought some programmers to the first Blockchain course at one of the most prestigious technology training institutions - Sophia Institute of Technology.\r\n\r\nDuring the storm of industrial revolution 4.0, Blockchain was considered a "key" technology for digital transformation and building future technology platforms.', 'en', NULL, NULL),
(150, '20200323094546', '4 người Hachinet đầu tiên nhận chứng chỉ đào tạo Blockchain uy tín', , 'Vào tháng 10 vừa qua, công ty Hachinet đã mạnh dạn đưa một số lập trình viên tham gia khóa học Blockchain đầu tiên tại một trong những cơ sở đào tạo công nghệ uy tín – Học viện công nghệ Sophia.\r\n\r\nTrong cơn bão cách mạng công nghiệp 4.0, Blockchain được xem là công nghệ “chìa khóa” cho chuyển đổi số và xây dựng nền tảng công nghệ tương lai. ', 'vn', NULL, NULL),
(154, '20200320091530', 'HACHINETが「VIETNAM IT DAY 2020」に参加', , '2020年02月06日に東京の大手町プレイスにて開催されました「 Vietnam IT day 2020」に参加いたしました。\r\n\r\nおかげさまで盛況のうちに無事終了することができました。これもひとえに皆様のご協力深謝申し上げます。', 'jp', NULL, 'HACHINET, VIETNAM IT DAY 2020'),
(155, '20200320091530', 'Hachinet attended the event "Vietnam IT day 2020"', , 'On February 6, 2020, Hachinet attended Vietnam IT day 2020 event held in Tokyo, Japan.\r\n\r\nThe event ended successfully and Hachinet would like to thank our customers, despite being busy, to but still take time discuss and learn about Hachinet''s software services.', 'en', NULL, NULL),
(156, '20200320091530', 'Hachinet tham gia sự kiện “Vietnam IT day 2020”', , 'Ngày 06 tháng 02 năm 2020, Hachinet đã tham dự sự kiện Vietnam IT day 2020 được tổ chức tại Tokyo, Nhật Bản. \r\n\r\nSự kiện đã kết thúc tốt đẹp và Hachinet xin chân thành cảm ơn quý khách hàng dù bận rộn nhưng vẫn dành thời gian tới trao đổi và tìm hiểu về dịch vụ phần mềm của Hachinet.', 'vn', NULL, NULL),
(157, '20200319050719', '日本が新たな移民政策を交付', , '現在、日本の経済における一番大きな問題は高齢化である。日本政府によると、日本人口は3年連続で減少していき、2014年の人口が1億2700万人になるという。それに加え、日本の歴史で初めて、65歳以上の人口の割合は記録的な25％まで増加し、2060年までに40％に達する可能性がある。', 'jp', NULL, NULL),
(158, '20200319050719', 'Japan enacted a new immigration policy', NULL, NULL, 'en', NULL, NULL);
