INSERT INTO `hcn_post_details` (`id`, `postId`, `postTitle`, `postContent`, `postDesc`, `postLang`, `postTags`, `postKeywords`) VALUES
(240, '20200326152105', 'Tăng cường các biện pháp biên giới của chính phủ Nhật Bản tại Việt Nam đối với các trường hợp nhiễm coronavirus mới', , 'Tăng cường các biện pháp biên giới của chính phủ Nhật Bản tại Việt Nam đối với các trường hợp nhiễm coronavirus mới', 'vn', NULL, NULL),
(241, '20200324085434', '【第29回Japan IT Week 春、幕張メッセにて延期開催のお知らせ】', , '第29回Japan IT Week 春の延期開催をお知ら致します。\r\n\r\n2020年4月8日ー10日に東京ビックサイトにて開催を予定しておりました。\r\n「第29回Japan IT Week 春」につきまして、事務局では、新型コロナウイルスに関する様々な事情を勘案し、関係者と協議を重ねた結果、開催の延期を決定させていただきました。', 'jp', NULL, NULL),
(242, '20200324085434', 'ANNOUNCEMENT: 29th Japan IT Spring Week 2020 Event IS TEMPORARILY SUSPENDED.', , 'Hachinet is pleased to announce that the Prime Minister of Japan after considering the complicated situation of Coronavirus, together with the result of the negotiations with related parties, the organizer has the conclusion: Japan IT Spring Week 2020 event, expected to be held on April 8-10th, 2020, is officially suspended. ', 'en', NULL, NULL),
(243, '20200324085434', 'THÔNG BÁO: TẠM DỪNG TỔ CHỨC “SỰ KIỆN TUẦN LỄ IT MÙA XUÂ N NHẬT BẢN LẦN THỨ 29”', , 'Hachinet xin trân trọng thông báo, Thủ tướng chính phủ Nhật Bản sau khi xem xét tình hình phức tạp đang diễn ra của virus chủng mới Corona, cùng với kết quả của cuộc đàm phán với các bên liên quan, Ban tổ chức đã đưa đến quyết định:Sự kiện Tuần lễ IT Mùa xuân 2020 dự kiến tổ chức ngày 8-10/4/2020 chính thức bị hoãn lại.', 'vn', NULL, NULL),
(244, '20200323095425', 'JAPANーMEKONG経済協力カンファレンス', , '第5回日越の経済・文化交流プログラムにおいて、11月30日、ベトナム商工会議所(VCCI) 、及びホーチミン市の日本貿易振興機(JETRO) は、メコンデルタの13県の投資・観光振興センターと協力して、JAPANーMEKONG経済協力カンファレンスを開催した。', 'jp', NULL, NULL),
(245, '20200323095425', 'The Mekong - Japan Business Cooperation Forum', , 'On November 30th, Within the framework of the 5th Japan-Vietnam Cultural and Trading Exchange Program, Vietnam Chamber of Commerce and Industry (VCCI) and the Japan External Trade Organization (JETRO) in Ho Chi Minh City cooperated with the Club of Investment and Tourism Promotion Centers in 13 provinces and cities in the Mekong Delta to hold the Mekong - Japan Business Cooperation Forum.', 'en', NULL, '5th Japan-Vietnam Cultural and Trading Exchange Program, Japan External Trade Organization (JETRO), Japanese investors, cooperation'),
(246, '20200323095425', 'Diễn đàn Hợp tác Kinh doanh Mekong - Nhật Bản', , 'ong khuôn khổ Chương trình Giao lưu Văn hóa Thương mại Việt Nam -Nhật Bản lần thứ 5, ngày 30/11, Phòng thương mại và Công nghiệp Việt Nam (VCCI) cùng cơ quan Xúc tiến thương mại Nhật Bản (JETRO) tại TP Hồ Chí Minh phối hợp với Câu lạc bộ các Trung tâm xúc tiến Đầu tư và Du lịch 13 tỉnh, thành vùng ĐBSCL tổ chức Diễn đàn Hợp tác Kinh doanh Mekong - Nhật Bản. Hơn 400 đại biểu đến từ Trung ương, các địa phương của 2 nước cùng các chuyên gia kinh tế, doanh nghiệp của Việt Nam và Nhật Bản', 'vn', NULL, NULL);
INSERT INTO `hcn_post_details` (`id`, `postId`, `postTitle`, `postContent`, `postDesc`, `postLang`, `postTags`, `postKeywords`) VALUES
(247, '20200326150129', 'ベトナム国内における新型コロナウイルス関連発表（外出禁止等）', , '【ポイント】\r\n●報道によれば，ベトナム政府は，今後，\r\n①市民の外出を最大限制限する，\r\n②外出せざるを得ない場合，マスクを常に着用し，他人と接触する際にはできるだけ２メートルの間隔をあける，\r\n③２０名以上の会議・イベント等の開催を中止し，特に，事務所ビル，学校，病院等の周辺で１０人以上が集まることを禁止する，\r\n④ハノイ市，ホーチミン市等において，食料，薬品，医療サービスを除き，全ての各種サービスの商店等を閉店する，等の措置を求めるとのことです（以下２に詳細）。\r\n●ベトナム在住の方にあっては，ご注意ください。\r\n●本件に関し，現時点で当館が入手している情報は，記載のとおりです。', 'jp', NULL, '在ベトナム日本国大使館'),
(248, '20200326150129', 'Announcement of the Japanese embassy on the situation of the Corona virus', , 'Announcement of the Japanese embassy on the situation of the Corona virus', 'en', NULL, 'covid-19, mask, infection, service, measures, announcement'),
